# Calculate distance from all to the depot
def calculate_distance_from_depot(routes, distance_matrix):
    costs = []
    total_cost = 0
    for route in routes:
        route_total_cost = distance_matrix[0][route[0]]
        for i in range(len(route) - 1):
            route_total_cost += distance_matrix[route[i]][route[i + 1]]
        route_total_cost += distance_matrix[route[-1]][0]
        costs.append(route_total_cost)
        total_cost += route_total_cost
    return costs, total_cost

